INSERT INTO `news` VALUES ('264', 'Іван Франко: Беркут', '&#1047; &#1091;&#1082;&#1088;&#1080;&#1090;&#1086;&#1075;&#1086; &#1075;&#1085;&#1110;&#1079;&#1076;&#1072; &#1074; &#1089;&#1082;&#1072;&#1083;&#1080;&#1089;&#1090;&#1110;&#1081; &#1076;&#1077;&#1089;&#1100; &#1097;&#1110;&#1083;&#1080;&#1085;&#1110;<br />\r\n&#1047; &#1090;&#1103;&#1078;&#1082;&#1080;&#1084; &#1074;&#1110;&#1085; &#1088;&#1086;&#1079;&#1084;&#1072;&#1093;&#1086;&#1084; &#1088;&#1074;&#1086;&#1085;&#1091;&#1074;&#1089;&#1100; &#1087;&#1110;&#1076; &#1093;&#1084;&#1072;&#1088;&#1080; &#1089;&#1080;&#1085;&#1110; -<br />\r\n&#1047; &#1090;&#1072;&#1108;&#1084;&#1085;&#1080;&#1093; &#1084;&#1086;&#1074; &#1076;&#1078;&#1077;&#1088;&#1077;&#1083; &#1075;&#1085;&#1110;&#1074;&#1083;&#1080;&#1074;&#1072; &#1076;&#1091;&#1084;&#1082;&#1072; &#1088;&#1074;&#1077;&#1090;&#1100;&#1089;&#1103;,<br />\r\n&#1054;&#1073;&#1083;&#1077;&#1090;&#1080;&#1090;&#1100; &#1089;&#1074;&#1110;&#1090;, &#1110; &#1072;&#1078; &#1086; &#1085;&#1077;&#1073;&#1072; &#1079;&#1074;&#1110;&#1076; &#1086;&#1087;&#1088;&#1077;&#1090;&#1100;&#1089;&#1103;,<br />\r\n&#1030; &#1073;\'&#1108; &#1074;&#1072;&#1078;&#1082;&#1080;&#1084; &#1082;&#1088;&#1080;&#1083;&#1086;&#1084;, &#1076;&#1077; &#1083;&#1080;&#1096; &#1089;&#1103;&#1075;&#1085;&#1091;&#1090;&#1080; &#1079;&#1084;&#1086;&#1078;&#1077;,<br />\r\n&#1030; &#1079;&#1074;&#1077;: \" &#1044;&#1077; &#1087;&#1088;&#1072;&#1074;&#1076;&#1072; &#1090;&#1072;?  &#1044;&#1077; &#1090;&#1080;, &#1074;&#1077;&#1083;&#1080;&#1082;&#1080;&#1081; &#1073;&#1086;&#1078;&#1077;?<br />\r\n&#1042;&#1089;&#1110; &#1079;&#1086;&#1088;&#1110; &#1079;&#1073;&#1110;&#1075;&#1083;&#1072; &#1103;, &#1072;&#1090;&#1086;&#1084;&#1080; &#1074;&#1089;&#1110; &#1074; &#1087;&#1088;&#1080;&#1088;&#1086;&#1076;&#1110;<br />\r\n&#1055;&#1077;&#1088;&#1077;&#1096;&#1091;&#1082;&#1072;&#1083;&#1072; &#1089;&#1082;&#1088;&#1110;&#1079;&#1100;, &#1090;&#1077;&#1073;&#1077; &#1078; &#1089;&#1087;&#1110;&#