INSERT INTO `news` VALUES ('309', 'Автограф-сесія Еви Гати', 'Запрошуємо на автограф-сесію Еви Гати та презентацію нової її книги <a href=\"http://www.bak.lviv.ua//index.php?option=com_content&task=view&id=300&Itemid=14\">\r\n\"Коріння світла\"</a>, яка відбудеться 16 вересня о 14 год. на стенді видавництва (№234, 2-й поверх).', '2016-09-06', '320', '0');
