INSERT INTO `news` VALUES ('272', 'Газета «Друг Читача» для юних читачів', 'Газета «Друг Читача» на честь міжнародного свята дитячої літератури пропонує 10 новеньких українських книжок вітчизняних та закордонних авторів для юних читачів різного віку. <br /> Серед них книга Еви Гати \r\n <a href=\"http://www.bak.lviv.ua//index.php?option=com_content&task=view&id=258&Itemid=14\">\r\n\"Казки чарівного королівства Красолі\"</a>\r\n\r\n\r\n\r\n\r\n\r\n', '2014-08-22', '654', '0');
INSERT INTO `news` VALUES ('273', 'Нова книга Еви Гати - Стати сонцем', 'Вийшла з друку нова книга Еви Гати \r\n <a href=\"http://www.bak.lviv.ua//index.php?option=com_content&task=view&id=267&Itemid=14\">\r\n\"Стати сонцем\"</a>\r\n\r\n</p><p>&#1044;&#1086; &#1042;&#1072;&#1096;&#1086;&#1111; &#1091;&#1074;&#1072;&#1075;&#1080; &#1090;&#1072;&#1082;&#1086;&#1078;:\r\n&quot;<a href=\"http://www.bak.lviv.ua/index.php?option=com_content&task=view&id=142&Itemid=14\">\r\n&#1053;&#1072;&#1088;&#1086;&#1076;&#1078;&#1077;&#1085;&#1072; &#1091; &#1076;&#1078;&#1077;&#1088;&#1077;&#1083;&#1110;, &#1072;&#1073;&#1086; &#1096;&#1083;&#1103;&#1093; &#1074;&#1110;&#1076; &#1062;&#1072;&#1088;&#1089;&#1090;&#1074;&#1072; &#1076;&#1086; &#1050;&#1086;&#1088;&#1086;&#1085;&#1080;&quot;</a>&nbsp;,\r\n<a href=\"http://www.bak.lviv.ua//index.php?option=com_content&task=view&id=216&Itemid=11\">\r\n\"&#1058;&#1091;&#1090; &#1110; &#1058;&#1072;&#1084;, &#1072;&#1073;&#1086; &#1089;&#1090;&#1077;&#1078;&#1082;&#1072; &#1084;&#1110;&#1078; &#1055;&#1072;&#1088;&#1085;&#1072;&#1089;&#1086;&#1084; &#1110; &#1054;&#1083;&#1110;&#1084;&#1087;&#1086;&#1084;\" &#1110; \"&#1058;&#1088;&#1080; &#1095;&#1077;&#1089;&#1085;&#1086;&#1090;&#1080;, &#1072;&#1073;&#1086; &#1082;&#1072;&#1079;&#1082;&#1072; &#1087;&#1088;&#1086; &#1086;&#1088;&#1110;&#1093;&#1072;&#1083;&#1082;\"</a>,\r\n<a href=\"index.php?option=com_content&amp;task=view&amp;id=83&amp;Itemid=14\">&quot;&#1054;&#1095;&#1080;&#1097;&#1077;&#1085;&#1085;&#1103;, &#1072;&#1073;&#1086; &#1088;&#1086;&#1073;&#1083;&#1102;, &#1097;&#1086; &#1093;&#1086;&#1095;&#1091;&quot;</a>, &quot;<a href=\"http://www.bak.lviv.ua/index.php?option=com_content&task=view&id=166&Itemid=14\">&#1041;&#1083;&#1072;&#1085;&#1110;&#1082; &#1074;&#1110;&#1076;&#1095;&#1080;&#1085;&#1103;&#1108; &#1076;&#1074;&#1077;&#1088;&#1110;&quot;</a>&nbsp;, <a href=\"index.php?option=com_content&amp;task=view&amp;id=173&amp;Itemid=14\">&quot;Katharsis, oder was ich will, das